@app.callback(
    Output("case-ch-graph", "figure"), [Input("radio-scale-switzerland", "value")],
)
def update_case_ch_graph(selected_scale):
    return {
        "data": [
            {
                "x": data.swiss_cases.iloc[:-2]["Date"],
                "y": data.swiss_cases.iloc[:-2]["CH"],
                "name": "CH",
                "mode": "lines",
                "marker": {"color": style.theme["foreground"]},
                "showlegend": False,
            },
            {
                "x": data.swiss_cases.iloc[-3:]["Date"],
                "y": data.swiss_cases.iloc[-3:]["CH"],
                "name": "CH",
                "mode": "lines",
                "line": {"dash": "dot"},
                "marker": {"color": style.theme["foreground"]},
                "showlegend": False,
            },
        ],
        "layout": {
            "title": "Total Reported Cases Switzerland",
            "height": 400,
            "xaxis": {"showgrid": True, "color": "#ffffff", "title": "Date"},
            "yaxis": {
                "type": selected_scale,
                "showgrid": True,
                "color": "#ffffff",
                "rangemode": "tozero",
                "title": "Reported Cases",
            },
            "hovermode": "closest",
            "dragmode": False,
            "plot_bgcolor": style.theme["background"],
            "paper_bgcolor": style.theme["background"],
            "font": {"color": style.theme["foreground"]},
        },
    }


